import { useContext, useEffect, useState } from 'react'
import { UserContext, api } from '../context/userContext'

export const HomePage = () => {
  const [img, setImg] = useState(img);
  // const[genre, setGenre] = useState('');
  const [artist, setArtist] = useState('');
  const [title, setTitle] = useState('');
  const [listener, setListener] = useState('');

  const getRandomMusic = async () => {
    try {
      const res = await api.get('api/songs/songs/random')
      setImg(res.data.cover_image);
      // setGenre(res.data.genre);
      setArtist(res.data.artist);
      setTitle(res.data.title);
      setListener(res.data.listener);
    } catch (err) {
      console.log('Erro: ', err);
    }
  }
  return (
    <div className="App">
      <header className="App-header">
        <img src={img} className="spotify-logo" alt="logo" />
        <img src={img} className="App-logo" alt="capa" />
        {/* <p>
          {genre === ''?
            '' :
            `GÃªnero: ${genre}`}
        </p> */}
        <p>
          {title === '' ?
            '' :
            `TÃ­tulo: ${title} `}

        </p>
        <p>
          {artist === '' ?
            '' :
            `Artista: ${artist} `}

        </p>
        <p>
          {listener === '' ?
            '' :
            `Ouvinte: ${listener} `}

        </p>
        <button
          className="App-link"
          onClick={getRandomMusic}
          target="_blank"
          rel="noopener noreferrer"
        >
          ðŸ”€
        </button>
      </header>
    </div>
  )
}
